pragma solidity ^ 0.4 .17;

contract EcommerceStore {

  struct Bid {
    address bidder; // 拍卖的人的钱包地址
    uint productId; // 产品id
    uint value; // 拍卖出的价格
    bool revealed; // 是否公告
  }


  enum ProductStatus {
    Open, //拍卖中
    Sold, //已经拍卖结束
    Unsold // 还没开始拍卖
  }
  enum ProductCondition {
    New, //新
    Used //旧
  }

  // 统计拍卖的产品的数量
  uint public productIndex;
  mapping(address => mapping(uint => Product)) stores;
  mapping(uint => address) productIdInStore;

  struct Product {
    uint id; //产品ID
    string name; // 产品名字
    string category; //产品分类
    string imageLink; //产品图片在ipfs上返回回来的hash
    string descLink; //产品描述信息在ipfs上返回回来的hash
    uint auctionStartTime; // 拍卖的时间
    uint auctionEndTime; // 拍卖结束时间
    uint startPrice; // 起始拍卖价格
    address highestBidder; // 最高拍卖人的地址
    uint highestBid; // 当前的产品公告以后最终统计出来的最高的价格
    uint secondHighestBid; // 第二高的人出的拍卖价格

    uint totalBids; // 一共多少人来参与拍卖
    ProductStatus status; // 产品拍卖的状态
    ProductCondition condition; // 产品是新的还是旧的

    mapping(address => mapping(bytes32 => Bid)) bids;
  }

  function EcommerceStore() public {
    productIndex = 0; //0
  }



  // 添加产品到合约里面
  function addProductToStore(string _name, string _category, string _imageLink, string _descLink, uint _auctionStartTime,
    uint _auctionEndTime, uint _startPrice, uint _productCondition) public {

    // 断言：判断拍卖结束时间必须大于拍卖起始时间
    require(_auctionStartTime < _auctionEndTime);
    // 每增加一款产品，productIndex，计数器
    productIndex += 1;
    // 创建一个产品的结构体对象
    Product memory product = Product(productIndex, _name, _category, _imageLink, _descLink, _auctionStartTime, _auctionEndTime,
      _startPrice, 0, 0, 0, 0, ProductStatus.Open, ProductCondition(_productCondition));

    //存储拍卖的产品的产品的信息
    stores[msg.sender][productIndex] = product;
    // 1 -> 0xf550abc
    productIdInStore[productIndex] = msg.sender;
  }

  // 通过id读取产品的数据信息
  function getProduct(uint _productId) view public returns(uint, string, string, string, string, uint, uint, uint, ProductStatus, ProductCondition) {
    Product memory product = stores[productIdInStore[_productId]][_productId];
    return (product.id, product.name, product.category, product.imageLink, product.descLink, product.auctionStartTime,
      product.auctionEndTime, product.startPrice, product.status, product.condition);
  }

  // 投标
  function bid(uint _productId, bytes32 _bid) payable public returns(bool) {
    // 获取产品信息
    Product storage product = stores[productIdInStore[_productId]][_productId];
    // 当前时间必须大于启始拍卖时间
    require(now >= product.auctionStartTime);
    // 当前时间必须小于结束时间
    require(now <= product.auctionEndTime);
    // msg.value 迷惑别人的价格
    require(msg.value > product.startPrice);

    // mapping (address => mapping (bytes32 => Bid)) bids;
    // {0xf55 -> {0xfadfasfasl: Bid}}
    // 3 + zhaohui
    require(product.bids[msg.sender][_bid].bidder == 0);
    // 存储这一次竞标的人的相关信息
    product.bids[msg.sender][_bid] = Bid(msg.sender, _productId, msg.value, false);
    product.totalBids += 1; // 竞标的次数
    return true;
  }


  // 30 3

  // 公告
  // 赵辉 0xF055775eBD516e7419ae486C1d50C682d4170645 3 zhaohui 5 100
  // 老麻 0xf17f52151EbEF6C7334FAD080c5704D77216b732 5 laoma   8 100
  // yunbo 0x627306090abaB3A6e1400e9345bC60c78a8BEf57 2 yunbo 2.5
  // 揭标 产品id，产品id由前端提供
  // 对于辉哥和老麻公告来讲，你们需要传入两个值，一个是实际想支付的价格，一个是投标的秘钥
  function revealBid(uint _productId, string _amount, string _secret) public {
    // 获取你投标的产品的结构体信息
    Product storage product = stores[productIdInStore[_productId]][_productId];
    // 当前时间必须大于产品竞标结束时间
    require(now > product.auctionEndTime);
    // sealedBid 生成一个hash
    bytes32 sealedBid = sha3(_amount, _secret);
    // 获取投标人的相关信息
    Bid memory bidInfo = product.bids[msg.sender][sealedBid];
    require(bidInfo.bidder > 0); // 地址不为空
    require(bidInfo.revealed == false); // 现在的状态必须为未公告状态

    uint refund; // 判断返回多少钱
    // 将_amount转换为uint，3
    uint amount = stringToUint(_amount);
    // bidInfo.value 5

    if (bidInfo.value < amount) {
      // 无效的投标
      refund = bidInfo.value;
    } else {

      if (address(product.highestBidder) == 0) {
        product.highestBidder = msg.sender;
        product.highestBid = amount;
        product.secondHighestBid = product.startPrice;
        refund = bidInfo.value - amount; // 5 - 3，2
      } else {

        if (amount > product.highestBid) {
          // 交换j价钱
          product.secondHighestBid = product.highestBid;
          // 把所有钱返回给辉哥
          product.highestBidder.transfer(product.highestBid);
          // 老大变成老麻
          product.highestBidder = msg.sender;
          // 5
          product.highestBid = amount;
          // 8 - 5 = 3
          refund = bidInfo.value - amount;
        } else if (amount > product.secondHighestBid) {
          product.secondHighestBid = amount;
          refund = amount;
        } else {
          refund = amount;
        }
      }

      if (refund > 0) {
        // 退还多余的代币
        msg.sender.transfer(refund);
        // 公告成功
        product.bids[msg.sender][sealedBid].revealed = true;
      }
    }
  }

  // 返回老大和老二的数据信息
  function highestBidderInfo(uint _productId) view public returns(address, uint, uint) {
    Product memory product = stores[productIdInStore[_productId]][_productId];
    return (product.highestBidder, product.highestBid, product.secondHighestBid);
  }

  // 接收到多少次投标
  function totalBids(uint _productId) view public returns(uint) {
    Product memory product = stores[productIdInStore[_productId]][_productId];
    return product.totalBids;
  }

  // 将string转换为uint类型
  function stringToUint(string s) pure private returns(uint) {
    bytes memory b = bytes(s);
    uint result = 0;
    for (uint i = 0; i < b.length; i++) {
      if (b[i] >= 48 && b[i] <= 57) {
        result = result * 10 + (uint(b[i]) - 48);
      }
    }
    return result;
  }


}
